    <script>
      var countDownDateResepsi = new Date("May 15, 2022 17:00:00").getTime();
      var countDownDateUnduhMantu = new Date("May 15, 2022 16:00:00").getTime();
      // Update the count down every 1 second
      setInterval(function() {

        // Get today's date and time
        var now = new Date().getTime();

        // Find the distance between now and the count down date
        var distanceR = countDownDateResepsi - now;
        var distance = countDownDateUnduhMantu - now;

        // Time calculations for days, hours, minutes and seconds
        var daysR = Math.floor(distanceR / (1000 * 60 * 60 * 24));
        var hoursR = Math.floor((distanceR % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        var minutesR = Math.floor((distanceR % (1000 * 60 * 60)) / (1000 * 60));
        var secondsR = Math.floor((distanceR % (1000 * 60)) / 1000);

        var days = Math.floor(distance / (1000 * 60 * 60 * 24));
        var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
        var seconds = Math.floor((distance % (1000 * 60)) / 1000);

        // Display the result in the element with id="demo"
        $(".resepsi .day .counter-head").html(daysR);
        $(".resepsi .hour .counter-head").html(hoursR);
        $(".resepsi .minutes .counter-head").html(minutesR);
        $(".resepsi .second .counter-head").html(secondsR);

        $(".unduh-mantu .day .counter-head").html(days);
        $(".unduh-mantu .hour .counter-head").html(hours);
        $(".unduh-mantu .minutes .counter-head").html(minutes);
        $(".unduh-mantu .second .counter-head").html(seconds);

        // If the count down is finished, write some text
        if (distanceR < 0) {
          clearInterval(x);
          document.getElementById("demo").innerHTML = "EXPIRED";
        }
      }, 1000);
    </script>
