    <script>
    var x = document.getElementById("audio");
    $(".playAudio").on("click", function(){
      if($('#audio').hasClass('paused')){
        $('#audio').removeClass('paused');
        $("#mute-sound").show();
        $("#unmute-sound").hide();
        x.pause();
      }else{
        $('#audio').addClass("paused");
        $("#mute-sound").hide();
        $("#unmute-sound").show();
        x.play();
      }
    });
    </script>
